import React from "react";

class Pokemons extends React.Component {
    render(){
      console.log(this.props.pokemon)
       const { value, measurementUnit} = this.props.pokemon.averageWeight
       return(
         <div className="card-pokemon">
           <p className="row-info"> {`name: ${this.props.pokemon.name}`} </p>
           <p className="row-info"> {`type: ${this.props.pokemon.type}`} </p>
           <p className="row-info">{`averageWeight: ${value + measurementUnit}`}</p>
           <img className src={ this.props.pokemon.image } alt={this.props.pokemon.name}></img>
         </div>
        )
    }
}

export default Pokemons;
